sourcesContent:
import phillips from '../../assets/partners/phillips.webp'
import affinity501 from '../../assets/catalog/uzi/phillips/affinity501.jfif'
import affinity502 from '../../assets/catalog/uzi/phillips/affinity502.jfif'
import affinity503 from '../../assets/catalog/uzi/phillips/affinity503.jfif'
import affinity504 from '../../assets/catalog/uzi/phillips/affinity504.jfif'
import affinity505 from '../../assets/catalog/uzi/phillips/affinity505.webp'
import oss1 from '../../assets/catalog/uzi/phillips/oss1.webp'
import oss2 from '../../assets/catalog/uzi/phillips/oss2.webp'
import oss3 from '../../assets/catalog/uzi/phillips/oss3.webp'
import oss4 from '../../assets/catalog/uzi/phillips/oss4.webp'
import epiq71 from '../../assets/catalog/uzi/phillips/epiq71.webp'
import epiq72 from '../../assets/catalog/uzi/phillips/epiq72.webp'
import epiq73 from '../../assets/catalog/uzi/phillips/epiq73.webp'
import epiq74 from '../../assets/catalog/uzi/phillips/epiq74.webp'
import epiq75 from '../../assets/catalog/uzi/phillips/epiq75.webp'
import epiqelite1 from '../../assets/catalog/uzi/phillips/epiqelite1.jfif'
import epiqelite2 from '../../assets/catalog/uzi/phillips/epicelite2.jfif'
import epiqelite3 from '../../assets/catalog/uzi/phillips/epicelite3.webp'
import epiqelite4 from '../../assets/catalog/uzi/phillips/epicelite4.webp'
import oss5 from '../../assets/catalog/uzi/phillips/oss5.webp'
import oss6 from '../../assets/catalog/uzi/phillips/oss6.webp'
import oss7 from '../../assets/catalog/uzi/phillips/oss7.webp'
import oss8 from '../../assets/catalog/uzi/phillips/oss8.webp'
import cx501 from '../../assets/catalog/uzi/phillips/cx501.webp'
import cx502 from '../../assets/catalog/uzi/phillips/cx502.jfif'
import cx503 from '../../assets/catalog/uzi/phillips/cx503.webp'
import cx504 from '../../assets/catalog/uzi/phillips/cx504.webp'
import cx505 from '../../assets/catalog/uzi/phillips/cx505.webp'
import affinity701 from '../../assets/catalog/uzi/phillips/affinity701.jfif'
import affinity702 from '../../assets/catalog/uzi/phillips/affinity702.jpg'
import affinity703 from '../../assets/catalog/uzi/phillips/affinity703.png'
import oss9 from '../../assets/catalog/uzi/phillips/oss9.webp'



export const MainList = [
  {
    uzi: [
      {
        link: "/catalog/uzi-apparats/phillips",
        img: <img className="catalog__item-img" src={phillips} alt="сенатмед senatmed" />,
        list: [
          {
            linkItem: "/catalog/uzi-apparats/phillips/epiq7",
            img: <img class="uzi__item-img" src={epiq71} alt="senatmed сенатмед" />,
            from: 'Phillips Нидерланды',
            model: 'EPIQ 7',
            description: 
            <>
              <li className="uzi__description-item">Технология nSIGHT знаменует собой новый подход к формированию ультразвуковых изображений</li>
              <li className="uzi__description-item">Оптимизированный рабочий процесс благодаря великолепным эргономическим характеристикам</li>
              <li className="uzi__description-item">Технология Anatomical Intelligence: от изображений к решению</li>
              <li className="uzi__description-item">PureWave — дополнительные возможности для выполнения технически сложных исследований</li>
            </>,
            images: [
              {
                item: <img src={epiq71} alt="senatmed Сенатмед" />
              },
              {
                item: <img src={epiq72} alt="senatmed Сенатмед" />
              },
              {
                item: <img src={epiq73} alt="senatmed Сенатмед" />
              },
              {
                item: <img src={epiq74} alt="senatmed Сенатмед" />
              },
              {
                item: <img src={epiq75} alt="senatmed Сенатмед" />
              },
            ]
            ,
            subtitleItem: 'Ультразвуковая система премиального класса',
            itemDesc: "Архитектура системы EPIQ 7 является наиболее мощной из всех систем, когда-либо применявшихся в ультразвуковой диагностике — это относится ко всем аспектам получения и обработки изображений, благодаря чему эта область диагностики выходит на новый, более совершенный уровень своего развития.",
            oss: [
              {
                img: '',
                title: 'Технология nSIGHT знаменует собой принципиально новый подход к формированию ультразвуковых изображен',
                text: 'Фирменная архитектура Philips nSIGHT — совершенно новый подход к формированию ультразвуковых изображений, не требующий каких-либо компромиссов. В отличие от традиционных систем, в которых изображение формируется линия за линией, технология nSIGHT создает изображения с оптимальным разрешением уже на уровне пикселов. nSIGHT использует новый прецизионный формирователь луча и мощную систему параллельной обработки большого объема данных. Эта уникальная архитектура позволяет регистрировать огромный объем ультразвуковых данных, а затем создавать оптимально сфокусированные лучи, обеспечивающие высочайшее разрешение в каждой точке изображения — и все это в режиме реального времени.'
              },
              {
                img: '',
                title: 'Технология Anatomical Intelligence: от изображений к решению',
                text: 'Благодаря архитектуре EPIQ 7 уникальная разработка компании Philips, технология Anatomical Intelligence Ultrasound (AIUS), превращает ультразвуковой аппарат в активную адаптирующуюся систему. Использование специализированных средств моделирования органов (с помощью технологии xMATRIX) и количественного анализа позволило упростить проведение исследований, повысить их воспроизводимость и достичь нового уровня клинической информативности. Технология AIUS охватывает множество сфер от автоматизации повторяющихся действий до полностью автоматизированного анализа с минимальным вмешательством врача, позволяя получать необходимые результаты.'
              },
              {
                img: '',
                title: 'Оптимизированный рабочий процесс благодаря великолепным эргономическим характеристикам и потрясающей',
                text: 'Система EPIQ 7 полностью изменяет методы работы врачей, позволяя им получать ультразвуковые изображения великолепного качества. Простота использования, оптимальный рабочий процесс, эргономичность и маневренность. Мы полностью изменили способ взаимодействия с ультразвуковой системой, сохранив удобство управления и бесшумность работы.'
              },
              {
                img: '',
                title: 'PureWave — дополнительные возможности для выполнения технически сложных исследований',
                text: 'Технология PureWave стала самым большим прорывом в создании пьезоэлектрических датчиков за последние 40 лет. Чистые, однородные кристаллы PureWave на 85% эффективнее обычных пьезоэлектрических материалов и благодаря этому обладают уникальными рабочими характеристиками. Эта технология обеспечивает повышенную глубину проникновения при исследовании технически сложных пациентов и позволяет достичь превосходного пространственного разрешения.'
              }
            ]
          },
          {
            linkItem: "/catalog/uzi-apparats/phillips/epiq-elite",
            img: <img class="uzi__item-img" src={epiqelite1} alt="senatmed сенатмед" />,
            from: 'Phillips Нидерланды',
            model: 'EPIQ Elite',
            description: 
            <>
              <li className="uzi__description-item">Технология визуализации nSIGHT — это совершенно новый подход к ультразвуковым исследованиям</li>
              <li className="uzi__description-item">Технологии PureWave и xMATRIX для передовых ультразвуковых датчиков</li>
              <li className="uzi__description-item">24-дюймовый дисплей HD MAX для ультразвуковой визуализации превосходного качества</li>
              <li className="uzi__description-item">Технология визуализации MicroFlow для исключительной детализации при отображении кровотока</li>
              <li className="uzi__description-item">Технология обработки изображений нового поколения XRES Pro</li>
            </>,
            images: [
              {
                item: <img src={epiqelite1} alt="senatmed Сенатмед" />
              },
              {
                item: <img src={epiqelite2} alt="senatmed Сенатмед" />
              },
              {
                item: <img src={epiqelite3} alt="senatmed Сенатмед" />
              },
              {
                item: <img src={epiqelite4} alt="senatmed Сенатмед" />
              },
            ]
            ,
            subtitleItem: 'Новая эра в развитии ультразвуковых систем премиум-класса',
            itemDesc: "Ультразвуковая система Philips EPIQ Elite обеспечивает высокий уровень клинической эффективности, рабочего процесса и усовершенствованных интеллектуальных технологий, что позволяет решать наиболее сложные задачи, возникающие в настоящее время. Платформа EPIQ Elite предоставляет оптимальные решения для ультразвуковых исследований с использованием настраиваемых в соответствии с клиническими задачами инструментов, которые позволяют вывести точность диагностики на новый уровень. Широкий спектр датчиков позволят проводить исследования с большей клинической эффективностью. Откройте для себя новое поколение ультразвуковых систем Philips EPIQ Elite для точной диагностики с первого раза!",
            oss: [
              {
                img: <img src={oss5} alt="senatmed сенатмед" className="uzi-item-oss-item-img" />,
                title: 'Технология визуализации nSIGHT',
                text: 'Значительно превосходит стандартные технологии выполнения ультразвуковых исследований, позволяя вывести разрешение и четкость изображения на новый уровень. Эта запатентованная архитектура, включающая специальную технологию многоступенчатого точного формирования луча и технологию массово-параллельной обработки данных, позволяет собирать огромное количество акустических данных во время каждой операции передачи и выполнять цифровую реконструкцию луча наряду с математически оптимизированной фокальной обработкой. Это позволяет создавать изображения в реальном времени с исключительной частотой кадров, однородностью и степенью проникновения.'
              },
              {
                img: <img src={oss6} alt="senatmed сенатмед" className="uzi-item-oss-item-img" />,
                title: '24-дюймовый дисплей HD MAX',
                text: 'Этот новый монитор с 24-дюймовым дисплеем и эффектом погружения обеспечивает непревзойденное качество ультразвуковой визуализации благодаря сверхширокой гамме цветов глубиной 10 бит, которая позволяет использовать миллиарды цветов для точной цветопередачи. Кроме того, он обеспечивает высококонтрастный динамический диапазон и улучшенный уровень черного для тонкого очерчивания значений шкалы серого. Дисплей HD MAX обеспечивает превосходный обзор независимо от угла для просмотра клинических изображений с любого места в кабинете сканирования.'
              },
              {
                img: <img src={oss7} alt="senatmed сенатмед" className="uzi-item-oss-item-img" />,
                title: 'Технология обработки изображений нового поколения XRES Pro',
                text: 'При частоте кадров в режиме реального времени технология XRES Pro использует многопараметрические прецизионные фильтры, которые последовательно разделяют элементы изображения, анализируют эти данные, а затем применяют усовершенствованные алгоритмы для повышения резкости границ и переходных областей и обеспечения превосходной видимости тканей. Технология XRES Pro также обеспечивает улучшенную оценку морфологии бляшек. Технология XRES Pro позволяет регулировать все параметры для установки уровня усиления в соответствии с требованиями к клинической визуализации, что повышает точность диагностики практически для всех пациентов.'
              },
              {
                img: <img src={oss8} alt="senatmed сенатмед" className="uzi-item-oss-item-img" />,
                title: 'Технология визуализации MicroFlow',
                text: 'Данная технология предназначена для обнаружения в тканях анатомических структур с медленным и слабым кровотоком. Это специально разработанное решение позволяет преодолеть многие препятствия, связанные со стандартными методами, для обнаружения кровотока в мелких сосудах с высоким разрешением и минимальной степенью выраженности артефактов. Технология визуализации MicroFlow поддерживает высокую частоту кадров и качество 2D-изображений, применяя передовые методы подавления артефактов для визуализации анатомии мелких сосудов.'
              }
            ]
          },
